allow:
Bash(xargs perl -pi -e '
  # --- Phase A: .wsc → .wfg \(do LONGER extension first\) ---
  # Identifier renames
  s/\\bwsc_content\\b/wfg_content/g;
  s/\\bwsc_parser\\b/wfg_parser/g;
  s/\\bwsc_ast\\b/wfg_ast/g;
  s/\\bwsc_file\\b/wfg_file/g;
  s/\\bWscFile\\b/WfgFile/g;
  s/\\bparse_wsc\\b/parse_wfg/g;
  s/\\bvalidate_wsc\\b/validate_wfg/g;
  s/\\bminimal_wsc\\b/minimal_wfg/g;
  # Standalone wsc variable names \(param names, local vars\)
  s/\\bwsc\\b/wfg/g;
  # Extension string: .wsc → .wfg
  s/\\.wsc\(?![a-zA-Z0-9_]\)/\\.wfg/g;

  # --- Phase B: .ws → .wfs \(shorter extension, after .wsc is already gone\) ---
  # Identifier renames
  s/\\bws_parser\\b/wfs_parser/g;
  s/\\bparse_ws\\b/parse_wfs/g;
  s/\\bws_file\\b/wfs_file/g;
  # Extension string: .ws NOT followed by word char \(avoiding .wfl etc that already changed\)
  s/\\.ws\(?![a-zA-Z0-9_]\)/\\.wfs/g;
  # Bare extension name in match arms: """"ws"""" → """"wfs""""
  s/""""ws""""/""""wfs""""/g;
')
